  local B1=AA:match("(.-)NAME")
  local B3=AA:match("NAME.-%c(.*)")

  -- set trackstatechunk and include new-state
  if tracknumber~=-1 then
    B=reaper.SetTrackStateChunk(Mediatrack,B1.."\n"..str.."\n"..B3,false)
  else
    B=true
  end
  return B, B1.."\n"..str.."\n"..B3
end

