      end
    end
  end
  
  if movemarkers==true then
    ultraschall.MoveMarkersBy(endposition, reaper.GetProjectLength(), -delta, true)
  end
  ultraschall.MoveMediaItemsAfter_By(endposition, -delta, trackstring)
  return C,CCC
end

