    optional string title - the title of the messagebox
    optional integer type - which buttons shall be shown in the messagebox, in that order
                            - 0, OK
                            - 1, OK CANCEL
                            - 2, ABORT RETRY IGNORE
                            - 3, YES NO CANCEL
                            - 4, YES NO
                            - 5, RETRY CANCEL
                            - nil, defaults to OK
    optional string button1_caption - caption of the first button
    optional string button2_caption - caption of the second button
    optional string button3_caption - caption of the third button
  </parameters>
  <retvals>
    integer - the button pressed by the user
                           - -1, error while executing this function
                           - 1, Button 1
                           - 2, Button 2
                           - 3, Button 3
  </retvals>
  <chapter_context>
    User Interface
    Dialogs
  </chapter_context>
  <target_document>US_Api_Documentation</target_document>
  <source_document>ultraschall_functions_engine.lua</source_document>
  <tags>user interface, user, interface, input, dialog, messagebox</tags>
</US_DocBloc>
--]]
  if type(caption)~="string" then ultraschall.AddErrorMessage("MB", "caption", "must be a string", -1) return -1 end
  if title~=nil and type(title)~="string" then ultraschall.AddErrorMessage("MB", "title", "must be a string or nil", -2) return -1 end
  if mbtype~=nil and math.type(mbtype)~="integer" then ultraschall.AddErrorMessage("MB", "mbtype", "must be an integer or nil(defaults to 0)", -3) return -1 end
  if mbtype<0 or mbtype>5 then ultraschall.AddErrorMessage("MB","mbtype", "Must be between 0 and 5!", -4) return -1 end
  
  if button1_caption~=nil and type(button1_caption)~="string" then ultraschall.AddErrorMessage("MB", "button1_caption", "must be a string or nil", -5) return -1 end
  if button2_caption~=nil and type(button2_caption)~="string" then ultraschall.AddErrorMessage("MB", "button2_caption", "must be a string or nil", -6) return -1 end
  if button3_caption~=nil and type(button3_caption)~="string" then ultraschall.AddErrorMessage("MB", "button3_caption", "must be a string or nil", -7) return -1 end
  
  if button1_caption==nil then button1_caption="" end
  if button2_caption==nil then button2_caption="" end
  if button3_caption==nil then button3_caption="" end
  if mbtype==nil then mbtype=0 end
  if title==nil then title="" end
  local temptitle=reaper.genGuid("")  
  ultraschall.Main_OnCommandByFilename(ultraschall.Api_Path.."/Scripts/SetMessageBox_Helper_Script.lua", temptitle, title, mbtype, button1_caption, button2_caption, button3_caption)

  local answer=reaper.MB(caption, temptitle, mbtype)

  if mbtype==0 and answer==1 then return 1
  elseif mbtype==1 and answer==1 then return 1
  elseif mbtype==1 and answer==2 then return 2
  elseif mbtype==2 and answer==3 then return 1
  elseif mbtype==2 and answer==4 then return 2
  elseif mbtype==2 and answer==5 then return 3
  elseif mbtype==3 and answer==6 then return 1
  elseif mbtype==3 and answer==7 then return 2
  elseif mbtype==3 and answer==2 then return 3
  elseif mbtype==4 and answer==6 then return 1
  elseif mbtype==4 and answer==7 then return 2
  elseif mbtype==5 and answer==4 then return 1
  elseif mbtype==5 and answer==2 then return 2
  end

  return answer
end



function ultraschall.CreateValidTempFile(filename_with_path, create, suffix, retainextension)
--[[
<US_DocBloc version="1.0" spok_lang="en" prog_lang="*">
  <slug>CreateValidTempFile</slug>
  <requires>
    Ultraschall=4.00
    Reaper=5.40
    Lua=5.3
  </requires>
  <functioncall>string tempfilename = ultraschall.CreateValidTempFile(string filename_with_path, boolean create, string suffix, boolean retainextension)</functioncall>
  <description>
    Tries to determine a valid temporary filename. Will check filename_with_path with an included number between 0 and 16384 to create such a filename.
    You can also add your own suffix to the filename.
    
    The pattern is: filename_with_path$Suffix~$number.ext (when retainextension is set to true!)
    
    If you wish, you can also create this temporary-file as an empty file.
    
    The path of the tempfile is always the same as the original file.
    
    Returns nil in case of failure.
  </description>
  <retvals>
    string tempfilename - the valid temporary filename found
  </retvals>
  <parameters>
    string filename_with_path - the original filename
    boolean create - true, if you want to create that temporary file as an empty file; false, just return the filename
    string suffix - if you want to alter the temporary filename with an additional suffix, use this parameter
    boolean retainextension - true, keep the extension(if existing) at the end of the tempfile; false, just add the suffix~number at the end.
  </parameters>
  <chapter_context>
    File Management
    Helper functions
  </chapter_context>
  <target_document>US_Api_Documentation</target_document>
  <source_document>ultraschall_functions_engine.lua</source_document>
  <tags>filemanagement, create, temporary, file, filename</tags>
</US_DocBloc>
]]
  if type(filename_with_path)~="string" then ultraschall.AddErrorMessage("CreateValidTempFile","filename_with_path", "Must be a string!", -2) return nil end
  if type(create)~="boolean" then ultraschall.AddErrorMessage("CreateValidTempFile","create", "Must be boolean!", -3) return nil end
  if type(suffix)~="string" then ultraschall.AddErrorMessage("CreateValidTempFile","suffix", "Must be a string!", -4) return nil end
  if type(retainextension)~="boolean" then ultraschall.AddErrorMessage("CreateValidTempFile","retainextension", "Must be boolean!", -5) return nil end
  local extension, tempfilename, A
  if retainextension==true then extension=filename_with_path:match(".*(%..*)") end
  if extension==nil then extension="" end
  for i=0, 16384 do
    tempfilename=filename_with_path..suffix.."~"..i..extension
    if reaper.file_exists(tempfilename)==false then
      if create==true then 
        A=ultraschall.WriteValueToFile(tempfilename,"")
        if A==1 then return tempfilename end
      elseif create==false then 
        return tempfilename
      end
    end
  end
  ultraschall.AddErrorMessage("CreateValidTempFile","filename_with_path", "Couldn't create a valid temp-file!", -1)
  return nil
end



