      if Table1[i][a]["source"]~=Table2[i][a]["source"] then return false end
      if Table1[i][a]["pan_law"]~=Table2[i][a]["pan_law"] then return false end
      if Table1[i][a]["volume"]~=Table2[i][a]["volume"] then return false end
    end
  end
  return true
end

