  num_integers, LeadInSilenceTrack = ultraschall.ConvertStringToIntegers(LeadInSilenceTrack, 4)
  BurnImage=string.byte(Decoded_string:sub(13,13))
  TrackMode=string.byte(Decoded_string:sub(17,17))
